function arrayUnique(arr) {
    var temp = new Array();
      arr.sort();
      for(i = 0; i < arr.length; i++) {
          if( arr[i] == arr[i+1]) {
            continue;
        }
          temp[temp.length]=arr[i];
      }
      return temp;
}
