function doConfirmDialog(options){
	var _class ,_class2;
	var _text = "";
	if(options.flag=='success'){
		_class="glyphicon-green";
		_class2 = "";
	}else if(options.flag=='error'){
		_class = "glyphicon-red";
		_class2 = "";
	}
	if(options.text) _text = options.text;
	var _buttons = {};
	if(options.buttons){
		if(options.buttons.ok){
			_buttons.ok = {text:'确定',btnClass:'btn btn-primary btn-sm'};
			if(options.buttons.ok.action){
				var _func = options.buttons.ok.action;
				_buttons.ok.action = eval(_func);
			}
		}
		if(options.buttons.cancel){
			_buttons.cancel = {text:'取消',btnClass:'btn btn-default btn-sm'};
			if(options.buttons.cancel.action){
				var _func = options.buttons.cancel.action;
				_buttons.cancel.action = eval(_func);
			}
		}
	}

	$.confirm({
    	title:false,
        content: '<div class="'+_class+' talign-center fz13">'+_text+'</div>',
        buttons: _buttons
    });
}
function doAlertDialog(options){
	var _text = "",_class="";
	if(options.text) _text = options.text;
	var _buttons = {
    		ok:{
    			"text":"知道了",
    			"btnClass":'btn btn-primary btn-sm'
    		}
    	};
	if(options.action){
		var _func = options.action;
		_buttons.ok.action = eval(_func);
	}
	$.alert({
    	title: false,
    	content: '<div class="'+_class+' talign-center fz13">'+_text+'</div>',
    	buttons:_buttons
	});
}
//全选,返选
function onSelectAll(obj){
	var _this = $(obj);
	var checked = _this.prop('checked');
	if (checked) {
        $("input[name='ids[]']:checkbox").not(':disabled').prop("checked", true);
	} else {
		$("input[name='ids[]']:checkbox").not(':disabled').prop("checked", false);
	}
}
function getScrollTop(){
	var scrollTop = 0, bodyScrollTop = 0, documentScrollTop = 0;
	if(document.body){
		bodyScrollTop = document.body.scrollTop;
	}
	if(document.documentElement){
		documentScrollTop = document.documentElement.scrollTop;
	}
	scrollTop = (bodyScrollTop - documentScrollTop > 0) ? bodyScrollTop : documentScrollTop;
	return scrollTop;
}
function getScrollHeight(){
	var scrollHeight = 0, bodyScrollHeight = 0, documentScrollHeight = 0;
	if(document.body){
		bodyScrollHeight = document.body.scrollHeight;
	}
	if(document.documentElement){
		documentScrollHeight = document.documentElement.scrollHeight;
	}
	scrollHeight = (bodyScrollHeight - documentScrollHeight > 0) ? bodyScrollHeight : documentScrollHeight;
	return scrollHeight;
}
function getWindowHeight(){
	var windowHeight = 0;
	if(document.compatMode == "CSS1Compat"){
		windowHeight = document.documentElement.clientHeight;
	}else{
		windowHeight = document.body.clientHeight;
	}
	return windowHeight;
}
